    private void calcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calcularActionPerformed
         // TODO add your handling code here:
        Tarea1 Datos = new Tarea1();
        
        Datos.setPeso(Double.parseDouble(TbPeso.getText()));
        Datos.setAltura(Double.parseDouble(TbAltura.getText()));
       
       Kilogramos.setText(String.valueOf(Datos.convertirPeso()));
       Metros.setText(String.valueOf((Datos.convertirAltura())));
       valueIMC.setText(String.valueOf(Datos.calcularIMC()));
       Mensaje.setText(Datos.viewResultado());
       
    }//GEN-LAST:event_calcularActionPerformed

